async function pageFunction(context) {
    const { $, request, response, log, skipLinks } = context;
    const { url } = request;

    if (response.status >= 400) throw new Error(`URL: ${url} response status ${response.status}`);

    handlePagination($);

    $.prototype.match = function(pattern) {
        if (this.length > 0 && pattern) {
            return this.filter((i, el) => $(el).text().search(pattern) > -1);
        }
        return this;
    };
    $.prototype.firstText = function() {
        const contents = this.first().text();
        return contents ? contents.replace(/\s\s+/g, ' ').trim() : '';
    };
    $.prototype.eachText = function(joinWith) {
        const result = [];
        this.each((i, el) => result.push($(el).firstText()));
        if (joinWith) return result.join(joinWith);
        return result;
    };
    String.prototype.extract = function(pattern) {
        const match = this.match(pattern);
        if (match && match.length > 0) return match[0];
        return '';
    }

    // Check we can get an ID
    const id = $('.wine-detail button.cart-add').attr('data-product-id');
    if (!id) {
        log.debug(`URL: ${url} no ID found`);
        return;
    }
    log.debug(`URL: ${url} ID: ${id}`);
    const result = {
        crawlName: 'naked',
        url: request.url,
        productId: id.trim(),
    };

    result.name = $('.wine-detail strong[itemprop="name"]').firstText();
    result.size = $('.wine-detail .tab-content span')
        .match(/(?:\d+\s*x\s*)*(?:\d*\.)?(?:\d+)\s*(?:[cm]*(?:enti|illi)*li?t?r?e?r?s?)*/i)
        .firstText();
    result.style = $('.wine-detail .tab-content a[href*="wines/style"]').firstText();
    result.description = $('.wine-detail ul.list__tick-points li').firstText();
    result.abv = $('.wine-detail .tab-content a[href*="filterWineAlcohol"]').firstText();
    result.country = $('.wine-detail .tab-content a[href*="wines/country"]').firstText();
    result.grapes = $('.wine-detail .tab-content a[href*="wines/grape"]').firstText();
    result.region = $('.wine-detail .tab-content a[href*="wines/country"]').parent().match(/(?<=\().+?(?=\))/).firstText();
    result.rating = $('.wine-detail div[itemprop="ratingValue"]').firstText();
    result.rating = $('.wine-detail span[itemprop="reviewCount"]').firstText();
    result.vegan = $('.wine-detail a[href="/wines/vegan"]').match(/Vegan/i).length > 0;
    result.vegetarian = $('.wine-detail a[href="/wines/vegetarian"]').match(/Vegetarian/i).length > 0;
    result.organic = $('.wine-detail a[href="/wines/organic"]').match(/Organic/i).length > 0;
    result.biodynamic = $('.wine-detail a[href="/wines/biodynamic"]').match(/Biodynamic/i).length > 0;
    result.bottleImage = $('.wine-detail img.bottle-shot').attr('src');
    result.bannerImage = ($('.wine-detail header').attr('style') || '').extract(/(?<=url\(').+?(?='\))/i);
    result.price = $('.wine-detail meta[itemprop="price"]').attr('content');
    result.offers = $('.wine-detail div.angel-price').first().eachText();
    result.available = $('.wine-detail div.form__joined--atb').length > 0;

    return result;

    function handlePagination($) {
        $('div.facts + div#load-more-container button#load-more-button').each((i, el) => {
            const href = $(el).attr('data-next-url');
            if (href) {
                const url = new URL(href, 'https://www.nakedwines.com/');
                context.enqueueRequest({ url: url.href });
            }
        });
    }
}
