async function pageFunction(context) {
    const { $, request, log } = context;
    const { url } = request;


    // jQuery extensions
    $.prototype.match = function(pattern) {
        if (this.length > 0 && pattern) {
            return this.filter((i, el) => ($(el).text().search(pattern) > -1));
        }
        return this;
    };
    $.prototype.firstText = function() {
        const contents = this.first().text();
        return contents ? contents.replace(/\s\s+/g, ' ').trim() : '';
    };
    $.prototype.eachText = function() {
        const result = [];
        this.each((i, el) => result.push($(el).firstText()));
        return result;
    }

    // Check we can get an ID
    const idMatch = url.match(/\/(\d+-\d+-\d+)/i);
    if (!idMatch) return;
    const id = idMatch[1];
    log.info(`URL: ${url} ID: ${id}`);
    const result = {
        crawlName: 'waitrose',
        url: request.url,
        productId: id,
    };

    result.name = $('h1#productName span[itemprop=name]').firstText();
    result.size = $('h1#productName span[class^=size]').firstText();
    result.abv = $('section#productDetails p[class^="label"]').match(/Alcohol by volume/i).children('span[class^="value"]').firstText();
    result.rating = $('p[class^="starRating"] span[itemprop="ratingValue"]').firstText();
    result.ratingCount = $('p[class^="starRating"] span[itemprop="reviewCount"]').firstText();
    result.description = $('section#marketingDescriptionBop').firstText();
    result.description2 = $('section#productDescription').firstText();
    result.country = $('section#productDetails h3[class^="title"]').match(/Origin Information/i).siblings('ul').children('li').firstText();
    result.breadcrumbs = $('nav[class^="breadcrumbs"] ul li[class^="crumb"] a').eachText();
    result.bottleImage = $('picture[class^="productDetails"] img[itemprop="image"]').attr('src');
    result.price = $('section[class^="productPricing"] span[data-test="product-pod-price"]').firstText();
    result.offers = $('section[class^="promotions"] div[class^="offer_"] a p').eachText();
    result.available = $('section[class^="addToTrolley"] div[class^="controlWithText"] button:disabled').length === 0;
    const lifestyles = $('section#productDetails h3[class^="title"]').match(/Lifestyle/i).siblings('ul').children('li');
    result.vegan = lifestyles.match(/Suitable for Vegans/i).length > 0;
    result.vegetarian = lifestyles.match(/Suitable for Vegetarians/i).length > 0;
    result.organic = $('li[data-type="organic"]').length > 0;
    result.fairtrade = $('li[data-type="fairtrade"]').length > 0;

    return result;
}
